import React, { useState } from 'react';
import { Card, CardContent, Typography, TextField, IconButton, Tooltip} from '@mui/material';
import { Delete, Share } from '@mui/icons-material';
import ReactDraft from './reactDraft';

interface NoteProps {
  id: number;
  title: string;
  content?: string; // Optional content
  onUpdate: (content: string) => void;
  onDeleteNote: (id: number) => void; // Function to handle note deletion (pass note ID)
  onShare: () => void; // Function to handle note sharing logic
}

const NoteObject: React.FC<NoteProps> = ({id, title, content, onUpdate, onDeleteNote, onShare }) => {
  const [noteContent, setNoteContent] = useState<string>(content || "");

  const handleChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
    setNoteContent(event.target.value);
    onUpdate(event.target.value);
  };

  return (
    <Card sx={{  width: '80%'}}>
      <CardContent sx={{  height: '100%'}}>
        <Typography variant="h6">{title}</Typography>
        <Tooltip title="Delete">
            <IconButton onClick=  { () => onDeleteNote(id) }>
              <Delete color="error" />
            </IconButton>
          </Tooltip>
          <Tooltip title="Share">
            <IconButton onClick={onShare}>
              <Share />
            </IconButton>
          </Tooltip>
        <TextField
          label="Content"
          multiline
          fullWidth
          rows={4}
          value={noteContent}
          onChange={handleChange}
        />
        <ReactDraft></ReactDraft>
      </CardContent>
    </Card>
  );
};

export default NoteObject;
