import React from 'react';
import { List, ListItemText, ListItemIcon, ListItemButton, Button } from '@mui/material';
import { AddCircleOutlineRounded, FolderOpenOutlined } from '@mui/icons-material';
import axios from 'axios';
import Note from './Interfaces';

interface SidebarProps {
  notes: Note[];
  onNoteClick: (id: string) => void; // Function to handle note selection
}

const Sidebar: React.FC<SidebarProps> = ({ notes, onNoteClick }) => {

  const handleCreate = async (noteBody : Note) => {
    try {
        await axios.post('http://localhost:3000/notes/', noteBody);
    } catch (error) {
        console.error('Error deleting note:', error);
    }
    window.location.reload();
  };
  const fetchTotalNoteNumber = async () => {
    try {
        const response = await axios.get<Number>('http://localhost:3000/notes/length');
    } catch (error) {
        console.error('Error fetching notes:', error);
    }
};

  return (
    <List sx={{ width: 250, bgcolor: 'background.paper' }}>
      <Button
      component="label"
      role={undefined}
      variant="contained"
      tabIndex={-1}
      startIcon={<AddCircleOutlineRounded/>}
      onClick={() => handleCreate({
        id: "5",
        title: 'New Note',
        content: ''
      })}
      >
      Add A New Note
      </Button>
      {notes.map((note) => (
        <ListItemButton key={note.id} onClick={() => onNoteClick(note.id)}>
          <ListItemIcon>
            <FolderOpenOutlined />
          </ListItemIcon>
          <ListItemText primary={note.title} />
        </ListItemButton>
      ))}
    </List>
  );
};

export default Sidebar;
