import React, { useState } from 'react';
import { List, ListItemText, ListItemIcon, ListItemButton, Button } from '@mui/material';
import { AddCircleOutlineRounded, FolderOpenOutlined } from '@mui/icons-material';
import axios from 'axios';
import Note from './Interfaces';

interface SidebarProps {
  notes: Note[];
  onNoteClick: (id: number) => void; // Function to handle note selection
  onCreate: (title: string, content: string) => void;
}

const Sidebar: React.FC<SidebarProps> = ({ notes, onNoteClick, onCreate }) => {
  
  const [newTitle, setNewTitle] = useState('');
  const [newContent, setNewContent] = useState('');

  const handleCreate = async () => {
    if (!newTitle || !newContent) return;

    try {
      const response = await axios.post<Note>('http://localhost:3000/notes/', {
        title: newTitle,
        content: newContent,
      });
      console.log('Note created successfully:', response.data);
      onCreate(response.data.title, response.data.content);
      setNewTitle('');
      setNewContent('');
    } catch (error) {
      console.error('Error creating note:', error);
      // Handle errors appropriately
    }
    window.location.reload();
  };
  const fetchTotalNoteNumber = async () => {
        const response = await axios.get<number>('http://localhost:3000/notes/length');
        const oneMore = response.data + 1;
        return oneMore.toString();
  };


  return (
    <List sx={{ width: 250, bgcolor: 'background.paper' }}>
      <Button
      component="label"
      role={undefined}
      variant="contained"
      tabIndex={-1}
      startIcon={<AddCircleOutlineRounded/>}
      onClick={() => handleCreate()}
      >
      Add A New Note
      </Button>
      {notes.map((note) => (
        <ListItemButton key={note.id} onClick={() => onNoteClick(note.id)}>
          <ListItemIcon>
            <FolderOpenOutlined />
          </ListItemIcon>
          <ListItemText primary={note.title} />
        </ListItemButton>
      ))}
    </List>
  );
};

export default Sidebar;
