import React from 'react';
import { List, ListItemText, ListItemIcon, ListItemButton, Button } from '@mui/material';
import { AddCircleOutlineRounded, FolderOpenOutlined } from '@mui/icons-material';

interface Note {
  title: string;
  id: string; // Unique identifier for each note
}

interface SidebarProps {
  notes: Note[];
  onNoteClick: (id: string) => void; // Function to handle note selection
}

const Sidebar: React.FC<SidebarProps> = ({ notes, onNoteClick }) => {
  return (
    <List sx={{ width: 250, bgcolor: 'background.paper' }}>
      <Button
      component="label"
      role={undefined}
      variant="contained"
      tabIndex={-1}
      startIcon={<AddCircleOutlineRounded/>}
      >
      Add A New Note
      </Button>
      {notes.map((note) => (
        <ListItemButton key={note.id} onClick={() => onNoteClick(note.id)}>
          <ListItemIcon>
            <FolderOpenOutlined />
          </ListItemIcon>
          <ListItemText primary={note.title} />
        </ListItemButton>
      ))}
    </List>
  );
};

export default Sidebar;
