import './App.css';
import { Link } from 'react-router-dom';
import Note from './Note';
import { useState } from 'react';
import { AppBar, Box, Divider, Grid, Toolbar, Typography } from '@mui/material';
import Sidebar from './Sidebar';


// Sample note data (replace with your data fetching logic)
const notes = [
    { title: 'Note 1', id: 'note-1' },
    { title: 'Note 2', id: 'note-2' },
    { title: 'Note 3', id: 'note-3' },
];

const MainPage = () => {
    const [selectedNoteId, setSelectedNoteId] = useState<string | null>(null);

    const handleNoteClick = (id: string) => {
        setSelectedNoteId(id);
    };

    const handleDelete = (id: string) => {
        // Implement logic to delete the note with the given ID from your data store (e.g., database, local storage)
        console.log('Delete note:', id); // For demonstration purposes, replace with actual deletion logic
      };
      
      const handleShare = () => {
        // Implement logic to share the current note content (e.g., copy to clipboard, generate a shareable link)
        console.log('Share note'); // For demonstration purposes, replace with actual sharing logic
      };

    const selectedNote = notes.find((note) => note.id === selectedNoteId);

    return (
        <div>
            <AppBar position="static">
                <Toolbar variant="dense">
                    <Typography variant="h6" color="inherit">
                        My Notes
                    </Typography>
                </Toolbar>
            </AppBar>
            <Box sx={{ display: 'flex', height: '100vh' }}> {/* Set full viewport height */}
                <Grid container p= {2} >
                    <Grid item  > {/* Adjust width for sidebar as needed */}
                        <Sidebar notes={notes} onNoteClick={handleNoteClick} />
                    </Grid>
                    
                        {selectedNote && <Note title={selectedNote.title} content="" onUpdate={handleNoteClick} onDelete= {handleDelete} onShare={ handleShare} />} {/* Replace with actual content */}
                    
                </Grid>
            </Box>
        </div>
    );
};

export default MainPage;
