import './App.css';
import { Link } from 'react-router-dom';
import Note from './Note';
import { useState } from 'react';
import { AppBar, Box, Divider, Toolbar, Typography } from '@mui/material';
import Sidebar from './Sidebar';


// Sample note data (replace with your data fetching logic)
const notes = [
    { title: 'Note 1', id: 'note-1' },
    { title: 'Note 2', id: 'note-2' },
    { title: 'Note 3', id: 'note-3' },
];

const MainPage = () => {
    const [selectedNoteId, setSelectedNoteId] = useState<string | null>(null);

    const handleNoteClick = (id: string) => {
        setSelectedNoteId(id);
    };

    const selectedNote = notes.find((note) => note.id === selectedNoteId);

    return (
        <div>
            <AppBar position="static">
                <Toolbar variant="dense">
                    <Typography variant="h6" color="inherit">
                        My Notes
                    </Typography>
                </Toolbar>
            </AppBar>
            <Box sx={{ display: 'flex' }}>
                <Sidebar notes={notes} onNoteClick={handleNoteClick} />
                <Divider sx={{ flexGrow: 1 }} />
                {selectedNote && <Note title={selectedNote.title} content="" onUpdate={function (content: string): void {
                    throw new Error('Function not implemented.');
                }} />} {/* Replace with actual content */}
            </Box>
        </div>
    );
};

export default MainPage;
